import React, { useState } from 'react';
import './Projects.css'; // Import your CSS file for styling

const Projects = () => {
  // Define state to manage the expanded state of each project card
  const [expandedProject, setExpandedProject] = useState(null);

  // Function to handle click on a project card
  const handleProjectClick = (projectId) => {
    // Toggle the expanded state of the clicked project card
    setExpandedProject(expandedProject === projectId ? null : projectId);
  };

  // Array of projects (dummy data)
  const projects = [
    { 
      id: 1, 
      name: 'Amps - A chatbot', 
      description: 'Created a chatbot using NLP in Python3 and connected to yahoo weather api and mySql database using Rest API.',
      image: 'https://example.com/project1.jpg', // Add image URL for Project 1
      githubLink: 'https://github.com/user/project1' // Add GitHub link for Project 1
    },
    // Add similar objects for other projects
  ];

  return (
    <div id="projects-section" className="projects-section">
      <div className="project-list">
        {/* Map over projects array to generate project cards */}
        {projects.map(project => (
          <div key={project.id} className={`project-card ${expandedProject === project.id ? 'expanded' : ''}`} onClick={() => handleProjectClick(project.id)}>
            <div className="project-body">
              {/* Project Image */}
              <img src={project.image} alt={project.name} className="project-image" />
              {/* Project Title */}
              <h3>{project.name}</h3>
              {/* Project Description */}
              <p>{project.description}</p>
              {/* GitHub Link */}
              <a href={project.githubLink} target="_blank" rel="noopener noreferrer" className="github-link">GitHub</a>
              {expandedProject === project.id && (
                <div className="project-details">
                  {/* Additional details */}
                  <p>Details about {project.name}...</p>
                </div>
              )}
              {/* Add more details or links */}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Projects;
