// About.js
import React from 'react';
import './About.css';

const About = () => {
  return (
    <div className="about-container">
      <div className="profile">
        <h1>About Me</h1>
        <p>Seasoned software engineer renowned for exceptional problem-solving abilities, evidenced by solving over 1400 challenges on LeetCode. With a Master's degree in Computer Science and a strong background in both frontend and backend development, I bring a unique blend of technical expertise and analytical prowess to every project. From optimizing algorithms to crafting elegant solutions, my dedication to problem-solving excellence drives success in every endeavor.</p>
      </div>
      <div className="skills">
        <h2>Skills & Frameworks</h2>
        <ul>
          <li>HTML5 & CSS3</li>
          <li>JavaScript (ES6+)</li>
          <li>React.js</li>
          <li>Node.js</li>
          <li>Express.js</li>
          <li>Bootstrap</li>
          <li>Responsive Web Design</li>
          <li>Git & GitHub</li>
          <li>...</li>
        </ul>
      </div>
    </div>
  );
}

export default About;
