// src/components/LandingPage.js
import React from 'react';
import './LandingPage.css'; // Import your CSS file for styling
import profileImage from '../assets/profile.jpeg'; // Import your profile image
import UserDetails from './UserDetails'; // Import the UserDetails component

const LandingPage = () => {
  const scrollToProjects = () => {
    const projectsSection = document.getElementById('projects-section');
    projectsSection.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="landing-page">
      <div className="landing-page-content">
        <div className="profile-image-container">
          <img src={profileImage} alt="Profile" className="profile-image" />
        </div>
        <h1>Hi, I am <span style={{display: 'inline-block'}} class="rainbow">Ashish</span>.</h1>
        <h2>I am a Software Engineer excited to showcase my projects.</h2>
        <p>Masters from Arizona State University</p>
      <p>Email: example@example.com</p>
      <p>Phone: +1234567890</p>
      <p>Hobby: [Your hobby]</p>
        <UserDetails /> {/* Include the UserDetails component */}
        <button className="cta-button" onClick={scrollToProjects}>View Projects</button>
      </div>
    </div>
  );
}

export default LandingPage;
